
class USdollar:
    def __init__(self, num1):
        self.num1 = num1
        def rupee(self):
            return self.num1*85
        def euro(self):
            return self.num1*0.97
        def yen(self):
            return self.num1*157
        def yuan(self):
            return self.num1*6
        

